package exer;

public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// for문 활용		
		
		for(int i=0;i<5;i++) {
			System.out.println("안녕!");
		}
		
		
		
		int j = 0;
		
		while(j<5) {
			System.out.println("안녕~");
			j++;
		}
			
	}

}
